import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      items: [
        {
          id: 1,
          title: 'Ноутбук HP Pavilion 15-eg2020ua',
          img: 'HP_Pavilion.jpg',
          category: 'Laptops',
          price: '37299'
        },
        {
          id: 2,
          title: 'Смартфон Samsung Galaxy A24',
          img: 'Samsung_A24.jpg',
          category: 'Phones',
          price: '8999'
        },
        {
          id: 3,
          title: 'Смартфон Apple iPhone 13',
          img: 'iPhone13.jpg',
          category: 'Phones',
          price: '32599'
        },
        {
          id: 4,
          title: 'Телевізор TCL QLED 43C725',
          img: 'TCL.jpg',
          category: 'TV',
          price: '16999'
        },
        {
          id: 5,
          title: 'Маршрутизатор TP-LINK ARCHER C64',
          img: 'TP_Link.jpg',
          category: 'Electronics',
          price: '1399'
        },
        {
          id: 6,
          title: 'Навушники TECNO Buds 1',
          img: 'Tecno.jpg',
          category: 'Accessories',
          price: '599'
        },
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
  }
  render() {
    return (
      <div className="wrapper">
        <Header /*orders={this.props.orders}*/ />
        <Items items={this.state.items} onAdd={this.addToOrder} />
        <Footer />
      </div>
    );
  }
  addToOrder(item) {
    this.setState({orders: [...this.state.orders, item] }, () => {console.log(this.state.orders)})
  }
}

export default App;
